import React, { Component } from 'react'
import Chart from './Chart'

//TODO deletar

const data = {
    title: {
        text: 'My chart'
    },
    series: [{
        name: 'Profits',
        data: [1, 2, 3]
    }]
}

class LineChart extends Component {
    constructor(props) {
        super(props)
        this.state = {
            title: {
                text: 'My chart'
            },
            series: [{
                name: 'Profits',
                data: [1, 2, 3]
            }]
        }
    }

    updateChartData(newData) {
        this.setState((state) => ({
            series: [{
                name: state.series[0].data,
                data: newData
            }]
        }))
    }

    render() {
        return (
            <div>
                <Chart data={data} />
            </div>
        )
    }
}

export default LineChart
